INSERT INTO `think_webinfo` VALUES (1,'hunterhug','中小企业智能展示平台Beta','使用最新的WEB技术设计和建造一个智能的企业展示平台，只需要用户注册，进行简单配置操作，就可以快速拥有自己的企业官网，方便中小企业进行企业展示。同时，互联网平台型软件，可以省去中小企业管理服务器，管理数据库的麻烦，且极易进行功能扩展。最后，市场上这种平台还没有。','20160323/56f28cfd68c92.jpg','广东省海珠区广东财经大学二饭隔壁芒果树下蚂蚁窝旁',1,'13247391421','粤ICP备16008312号-1','网站正在努力开发中····'),(2,'hunter','DDD','DD','20160213/56bedbc433f1f.jpg','DD',1,'0768-6833066','DD',NULL),(3,'admin','brtb','rtb','20160217/56c497530527e.jpg','brtb',1,'0768-6833066','bbr',NULL),(4,'1234567','rrr','rrrrrr','20160217/56c497530527e.jpg','dd',1,'13247391421','ee',NULL),(5,'user1','马里奥工业有限公司','马里奥工业有限公司使用最新的WEB技术设计和建造一个智能的企业展示平台，只需要用户注册，进行简单配置操作，就可以快速拥有自己的企业官网，方便中小企业进行企业展示。同时，互联网平台型软件，可以省去中小企业管理服务器，管理数据库的麻烦，且极易进行功能扩展。最后，市场上这种平台还没有。','20160319/56ecf6a14fb7b.jpg','马里奥工业马里奥工业有限公司',1,'0258-6565656','马里奥工业有限公司马里奥工业有限公司','马里奥工业有限公司'),(6,'user2','哈士奇食品有限公司','请输入网站描述','20160319/56ecf732e7d74.jpg','请输入联系地址',1,'13247391421','请输入备案信息',NULL),(7,'user3','美少女玩具有限公司','请输入网站描述','20160319/56ecf7e1e7ec1.jpg','请输入联系地址',1,'13247391421','请输入备案信息',NULL),(8,'user4','名人包装股份有限公司','请输入网站描述','20160319/56ecf84a805d1.jpg','请输入联系地址',1,'13247391421','请输入备案信息',NULL),(9,'user5','兔斯基纹身结合部','请输入网站描述','20160319/56ecfa02f2250.jpg','请输入联系地址',1,'13247391421','请输入备案信息',NULL);
